import { Component, Input } from '@angular/core';
import { ServicioFavoritosService } from 'src/app/servicio-favoritos.service';
interface Tarjeta{
  titulo:string;
  subtitulo:string;
  image?:string;
}
@Component({
  selector: 'app-productos',
  templateUrl: './productos.component.html',
  styleUrls: ['./productos.component.css']
})
export class ProductosComponent {

  public ArregloTarjetas: Tarjeta[]=[];

  public image?:string;
  public Titulo:string = "Curso de Angular con Interpolación";
  @Input() dataEntrante:any;
  constructor(private servicioFavorito: ServicioFavoritosService) { }


  ngOnInit(): void {
    this.ArregloTarjetas = [
      {titulo:'Video 1', subtitulo: 'Subtitulo Video 1', image:"https://i.ytimg.com/vi/PSD5pFi6bx4/hq720.jpg?sqp=-oaymwEcCNAFEJQDSFXyq4qpAw4IARUAAIhCGAFwAcABBg==&rs=AOn4CLBRXrJxTLALTsBas1djapMjtW0lQg"},
      {titulo:'Video 2', subtitulo: 'Subtitulo Video 2', image:"https://i.ytimg.com/vi/rOsd3OI9dOA/hq720.jpg?sqp=-oaymwEcCNAFEJQDSFXyq4qpAw4IARUAAIhCGAFwAcABBg==&rs=AOn4CLBdMDLcELCLzlSzVoELr-QZ41mL8g"},
      {titulo:'Video 3', subtitulo: 'Subtitulo Video 3', image:"https://i.ytimg.com/vi/lasGrvRYBWg/hq720.jpg?sqp=-oaymwEcCNAFEJQDSFXyq4qpAw4IARUAAIhCGAFwAcABBg==&rs=AOn4CLDgh8fipV-k2f4xjEEPpvvtiWHwjA"},
     ]
     }

  AgregarAFavorito()
  {
    this.servicioFavorito.diparadorFavoritos.emit(
      {data: this.dataEntrante}
    )
  }



}
<app-header></app-header>

<div class="container container-md-fluid">
  <div class="row ">
      <div class="col-auto col-md-10  ">
          <nav aria-label="breadcrumb " class="first   d-md-flex bg-violet" >
              <ol class="breadcrumb indigo lighten-6 first-1 shadow-lg mb-5  ">
                  <li class="breadcrumb-item font-weight-bold"><a class="black-text text-uppercase" routerLink="/home"><span  >home</span></a><img class="ml-md-3" src="https://img.icons8.com/offices/100/000000/double-right.png" width="20" height="20"> </li>


              </ol>
          </nav>
      </div>
  </div>
</div>


<div class="card">
  <img class="image" [src]="dataEntrante.image" alt="">
  <div class="wrapper-text">
      <div class="title">{{dataEntrante.titulo}}</div>
      <div class="sub-title">{{dataEntrante.subtitulo}}</div>
  </div>
  <div>
      <button class="favorite" (click)="AgregarAFavorito()">Añadir a Favoritos</button>
  </div>
</div>
<div class = "grid">
     *ngFor="let card of ArregloTarjetas">
</div>






<app-footer></app-footer>
